mapper.select:
select b.no ,b.title ,b.content ,b.hit ,date_format(b.reg_date, "%Y-%m-%d") regDate ,u.no userNo ,u.name userName from users u, board b where u.no = b.user_no
and b.title like concat('%', #{kwd}, '%')
order by b.no asc limit #{startRowNo}, #{listCnt}

mapper.update:

update board
set title= #{title}
	,content= #{content}
where no = #{no}
